// LinkedIn Profile Search Query Builder - Profile Only
const input = items[0].json.output;
let totalLeads = 0;
let page=1;
// Extract and normalize data
const roles = input.roles || [];
const leadCount = input.lead_count || input.leadCount || 10;
const filters = input.filters || [];
const location = input.location || '';
const keywords = input.keywords || [];

// Helper function to create quoted phrases with OR logic
function createQuotedTerms(terms) {
  return terms.filter(term => term && term.trim())
              .map(term => `"${term.trim()}"`)
              .join(' OR ');
}

// Helper function to build location query
function buildLocationQuery(location) {
  if (!location) return '';

  // Handle multiple locations separated by OR, comma, or "or"
  const locationTerms = location.split(/\s+(?:OR|or)\s+|,\s*/)
    .map(loc => loc.trim())
    .filter(loc => loc);

  if (locationTerms.length === 1) {
    return `"${locationTerms[0]}"`;
  } else if (locationTerms.length > 1) {
    return `(${locationTerms.map(loc => `"${loc}"`).join(' OR ')})`;
  }
  return '';
}

// Build single LinkedIn profile search query
const roleQuery = createQuotedTerms(roles);
const locationQuery = buildLocationQuery(location);
const filterQuery = filters.length > 0 ? createQuotedTerms(filters) : '';
const keywordQuery = keywords.length > 0 ? createQuotedTerms(keywords) : '';

// Build LinkedIn profile query
let profileQuery = 'site:linkedin.com/in';
const queryParts = [];

if (roleQuery) queryParts.push(roleQuery);
if (locationQuery) queryParts.push(locationQuery);
if (filterQuery) queryParts.push(filterQuery);
if (keywordQuery && !roleQuery) queryParts.push(keywordQuery); // Only add keywords if no roles

if (queryParts.length > 0) {
  profileQuery += ` ${queryParts.join(' ')}`;
}

// Return single result for LinkedIn profiles only
return [{
  json: {
    searchQuery: profileQuery.trim(),
    leadCount:leadCount ,
    totalLeads:totalLeads,
    page:page,
    platform: 'linkedin'
  }
}];

// This array will hold all parsed leads
const leads = [];

// Simple regex to find emails in text
const emailRegex = /[a-zA-Z0-9._%+-]+@[a-zA-Z0-9.-]+\.[a-z]{2,}/gi;

// Loop over the 'organic_results' array from SerpAPI
for (const result of $('🌐 Google Search Engine').first().json.organic_results || []) {

  // Extract title, link, and snippet from each search result
  const nameAndRole = result.title || "";           // e.g. "Jane Doe - SaaS Founder"
  const link = result.link || "";                   // LinkedIn profile URL
  const connections = result.displayed_link || "";   // 590+ followers
  const snippet = result.snippet || "";             // Description snippet from Google
  const extensions = result.rich_snippet?.top?.extensions || [];  // Additional info, often includes company

  // Split title into name and role (based on " - ")
  const [namePart, rolePart] = nameAndRole.split(" - ");

  // Try to get company name from extensions (usually in 3rd position)
  let company = "";
  if (extensions.length >= 3 && typeof extensions[2] === "string") {
    company = extensions[2].trim(); // Safe trim only if it's a string
  }

  // Try to extract email from snippet using regex
  let emails = snippet.match(emailRegex);
  // Use the first email if available, else empty string
  let email = (emails && emails.length > 0) ? emails[0].trim() : "";

  // Push the cleaned lead data into the leads array
  leads.push({
    json: {
      name: namePart?.trim() || "",       // Safely trim name
      role: rolePart?.trim() || "",       // Safely trim role
      company: company,                   // Already trimmed
      connections: connections,
      linkedinUrl: link,
      email: email,                      // Extracted email if found
      snippet: snippet,
      scrapedAt: new Date().toISOString() // Timestamp when scraped
    }
  });
}

// Return all extracted leads to the next node
return leads;

// Fixed pagination logic
const newLeads = 10;
const linkedInData = $('🔍 LinkedIn Search Builder').first().json;
const prevData = $input.first()?.json || {};
const totalLeads = (linkedInData.totalLeads || 0) + newLeads;
const page = (prevData.page || 1) + 1;
const que = linkedInData.searchQuery;
return [{ json: { totalLeads, leadCount: linkedInData.leadCount, que, page } }];
